mapper.update:
update
version = #{version}+1, name = #{name,jdbcType=VARCHAR}, url = #{url,jdbcType=VARCHAR}, parent_id = #{parent.id,jdbcType=VARCHAR}, number = #{number,jdbcType=VARCHAR}, is_leaf = #{isLeaf,jdbcType=VARCHAR}, level =#{level,jdbcType=INTEGER}, target_name = #{targetName,jdbcType=VARCHAR}
where ID = #{id} and VERSION = #{version}

mapper.select:
select d.id,d.version,d.status,d.create_user_name as "createUserName", d.create_time as "createTime", d.update_user_name as "updateUserName", d.update_time as "updateTime", d.remark, d.name as "name", d.url as "url", d.parent_id as "parent.id", parent.name as "parent.name", d.number as "number", d.is_leaf as "isLeaf", d.level as "level", d.target_name as "targetName" from
d left join
parent on d.parent_id = parent.id where d.id = #{id}
